mapper.select:
SELECT A.hotel_pk AS hotelPk, A.hotel_nm AS hotelNm, B.pic AS hotelPic , hotelRoomCost, avgStar, A.discount_per AS discountPer, reviewCount , G.address_name AS addressName, IF(I.hotel_pk IS NULL, 0, 1) AS bookMark FROM t_hotel A LEFT JOIN t_hotel_pic B ON A.hotel_pk = B.hotel_pk LEFT JOIN t_reservation C ON A.hotel_pk = C.hotel_pk LEFT JOIN ( SELECT A.res_pk, round(AVG(A.score),1) AS avgStar, COUNT(A.review_pk) AS reviewCount FROM t_review A join t_reservation B ON A.res_pk = B.res_pk GROUP BY B.hotel_pk ) D ON C.res_pk = D.res_pk LEFT JOIN ( SELECT A.hotel_pk, MIN(A.hotel_room_cost) AS hotelRoomCost FROM t_hotel_room_info A JOIN t_hotel B ON A.hotel_pk = B.hotel_pk WHERE A.hotel_room_cost GROUP BY A.hotel_pk ) E ON A.hotel_pk = E.hotel_pk JOIN( SELECT A.hotel_pk FROM t_hotel A JOIN t_hotel_room_info B ON A.hotel_pk = B.hotel_pk WHERE B.size_pk IN
#{item}
GROUP BY A.hotel_pk HAVING COUNT(A.hotel_pk) >= #{dogPkSize} ) F ON A.hotel_pk = F.hotel_pk LEFT JOIN t_hotel_where G ON A.hotel_pk = G.hotel_pk
LEFT JOIN t_user H ON A.user_pk = H.user_pk LEFT JOIN t_hotel_favorites I ON A.hotel_pk = I.hotel_pk AND I.user_pk = #{userPk}
WHERE G.region_1depth_name = #{address} GROUP BY A.hotel_pk
ORDER BY A.created_at DESC
ORDER BY avgStar DESC
ORDER BY reviewCount DESC
LIMIT #{startIdx}, #{rowCount}

