mapper.update:
UPDATE t_review
SET comment = #{comment}
,score = #{score}
WHERE review_pk = #{reviewPk}

UPDATE t_review
SET comment = #{comment}
WHERE review_pk = #{reviewPk} AND res_pk = #{resPk}

mapper.select:
SELECT res_status
FROM t_reservation
WHERE user_pk = #{userPK} AND res_pk = #{resPk}

SELECT user_pk
FROM t_reservation
WHERE user_pk = #{userPk} AND res_pk = #{resPk}
