mapper.insert:
INSERT INTO t_user
SET user_type_pk = #{userTypePk}
, user_email = #{userEmail}
, upw = #{upw}
, nickname = #{nickname}
, phone_num = #{phoneNum}
, user_address = #{userAddress}

mapper.select:
SELECT upw
FROM t_user
WHERE user_email = #{userEmail}

SELECT DISTINCT size_pk
FROM t_user_dog
WHERE user_pk = #{userPK}

SELECT region_1depth_name
FROM t_user_address
WHERE user_pk = #{userPk}

mapper.update:
UPDATE t_user
SET nickname = #{nickname}
,phone_num = #{phoneNum}
,user_address = #{userAddress}
WHERE user_pk = #{userPk}

UPDATE t_user_where
SET address_name = #{addressName}
,region_1depth_name = #{region1DepthName}
,region_2depth_name = #{region2DepthName}
,region_3depth_name = #{region3DepthName}
,zone_num = #{zoneNum}
,x_coordinate = #{x}
,y_coordinate = #{y}
,detail_address = #{detailAddress}
WHERE user_pk = #{userPk}
